ow admin_fullbar_box">						<form action="?run=menu_ajax.php&id=<?php echo hm_get('id'); ?>&action=edit" method="post" class="ajaxForm ajaxFormmenuEdit">				<div class="col-md-4">										<div class="form-group"> 						<a href="?run=menu.php&action=location" class="btn btn-primary"><?php echo hm_lang('manage_menu_location'); ?></a>					</div>										<div class="menu_object_box_list">						<?php						foreach($args_tax as $key => $val){						?>						<div class="menu_object_box">							<div class="menu_object_tab btn-default" data-id="<?php echo $key; ?>">								<span><?php echo $val['taxonomy_name']; ?></span>							</div>							<div class="menu_object_list_wapper">								<ul class="menu_object_list taxnomy_list tree" data-id="<?php echo $key; ?>">								<?php									$args['key']=$key;									taxonomy_checkbox_list($args); 								?>								</ul>							</div>						</div>						<?php						}						?>																		<div class="menu_object_box">							<div class="menu_object_tab btn-default" data-id="menu-registed-request">								<span><?php echo hm_lang('from_theme_and_plugin'); ?></span>							</div>							<div class="menu_object_list_wapper">								<div class="menu_object_list" data-id="menu-registed-request">									<ul class="registed_request_list tree" data-id="registed_request">									<?php										$registed_request = registed_request();										foreach($registed_request as $request => $request_data){											if(isset($request_data['args']['menu']) AND $request_data['args']['menu']==1){																								$request_label = $request;												if(isset($request_data['args']['name']) AND $request_data['args']['name']!=''){													$request_label = $request_data['args']['name'];												}													echo '<li data-slug="'.$request.'" class="tax_tree_item">';												echo '<input type="checkbox" name="registed_request[]" value="'.$request.'">';												echo $request_label;												