<?php/**  * Tệp tin xử lý request uri bằng ajax trong admin * Vị trí : admin/request_ajax.php  */if ( ! defined('BASEPATH')) exit('403');ini_set('display_errors', 0);if (version_compare(PHP_VERSION, '5.3', '>=')){	error_reporting(E_ALL & ~E_NOTICE & ~E_DEPRECATED & ~E_STRICT & ~E_USER_NOTICE & ~E_USER_DEPRECATED);}else{	error_reporting(E_ALL & ~E_NOTICE & ~E_STRICT & ~E_USER_NOTICE);}	/** gọi tệp tin admin base */require_once( dirname( __FILE__ ) . '/admin.php' );/** gọi model xử lý taxonomy */require_once( dirname( __FILE__ ) . '/request/request_model.php' );$key=hm_get('key');$id=hm_get('id');$action=hm_get('action');switch ($action) {		case 'suggest':		$key = hm_post('val');		echo request_suggest($key);	break;	case 'suggest_data':		$args = array();		$args['val'] = hm_post('val');		$args['key'] = hm_post('key');		$args['type'] = hm_post('type');		$args['input'] = hm_post('input');		echo suggest_data($args);	break;}