<?php/**  * Tệp tin xử lý giao diện trong admin * Vị trí : admin/theme.php  */if ( ! defined('BASEPATH')) exit('403');if(!defined('ALLOW_THEME_PAGE')){	define('ALLOW_THEME_PAGE',TRUE);}if(ALLOW_THEME_PAGE != TRUE){	hm_exit('Tính năng đã bị tắt');}/** gọi tệp tin admin base */require_once( dirname( __FILE__ ) . '/admin.php' );/** gọi model xử lý theme */require_once( dirname( __FILE__ ) . '/theme/theme_model.php' );$key=hm_get('key');$id=hm_get('id');$action=hm_get('action');switch ($action) {    case 'add':			$args = api_theme_data(FALSE,array('keyword'=>hm_post('keyword','')));		/** Hiển thị giao diện thêm plugin */		function admin_content_page(){			global $args;			require_once(BASEPATH . HM_ADMINCP_DIR . '/' . LAYOUT_DIR . '/' . 'theme_add.php');		}		    break;    default :				/** Hiển thị giao diện tất cả theme */		function admin_content_page(){			require_once(BASEPATH . HM_ADMINCP_DIR . '/' . LAYOUT_DIR . '/' . 'theme_all.php');		}}/** fontend */hm_admin_require_layout(BASEPATH . HM_ADMINCP_DIR . '/' . LAYOUT_DIR . '/' . 'layout.php');