<?php/**  * Tệp tin xử lý theme trong admin * Vị trí : admin/theme.php  */if ( ! defined('BASEPATH')) exit('403');if(!defined('ALLOW_THEME_PAGE')){	define('ALLOW_THEME_PAGE',TRUE);}if(ALLOW_THEME_PAGE != TRUE){	hm_exit('Tính năng đã bị tắt');}ini_set('display_errors', 0);if (version_compare(PHP_VERSION, '5.3', '>=')){	error_reporting(E_ALL & ~E_NOTICE & ~E_DEPRECATED & ~E_STRICT & ~E_USER_NOTICE & ~E_USER_DEPRECATED);}else{	error_reporting(E_ALL & ~E_NOTICE & ~E_STRICT & ~E_USER_NOTICE);}	/** gọi tệp tin admin base */require_once( dirname( __FILE__ ) . '/admin.php' );/** gọi model xử lý theme */require_once( dirname( __FILE__ ) . '/theme/theme_model.php' );$key=hm_get('key');$id=hm_get('id');$action=hm_get('action');switch ($action) {		case 'available':		echo available_theme();	break;		case 'active_theme':		$theme = hm_post('theme');		echo active_theme($theme);	break;		case 'add_theme':		$href = hm_post('href');		echo add_theme($href);	break;}